// SPDX-License-Identifier: MIT
// OpenZeppelin Contracts (last updated v5.0.0) (token/ERC20/extensions/ERC20Wrapper.sol)

pragma solidity ^0.8.20;

import {IERC20, IERC20Metadata, ERC20} from "@openzeppelin/contracts/token/ERC20/ERC20.sol";
import {SafeERC20} from "@openzeppelin/contracts/token/ERC20/utils/SafeERC20.sol";

import {Context} from "@openzeppelin/contracts/utils/Context.sol";
import {IERC20Errors} from "@openzeppelin/contracts/interfaces/draft-IERC6093.sol";

interface ICondition{
    function runCondition(bytes memory data) external returns(bool);
}

contract PurposeWrapper is Context, IERC20Errors {
    IERC20 public immutable _underlying;

    event Transfer(address indexed from, address indexed to, uint256 value);

    event Approval(address indexed owner, address indexed spender, uint256 value);

    struct Condition {
        uint256 id;
        uint256 amount;
        address condition_contract;
    }

    mapping (address => Condition[]) public user_to_conditions;

    mapping(address account => uint256) private _balances;

    mapping(address account => mapping(address spender => uint256)) private _allowances;

    uint256 private _totalSupply;

    string private _name;
    string private _symbol;

    /**
     * @dev The underlying token couldn't be wrapped.
     */
    error ERC20InvalidUnderlying(address token);

    constructor(IERC20 underlyingToken,string memory Name,string memory Symbol){
        if (address(underlyingToken) == address(this)) {
            revert ERC20InvalidUnderlying(address(this));
        }
        _underlying = underlyingToken;
        _name=Name;
        _symbol=Symbol;
    }

    function name() public view  returns (string memory) {
        return _name;
    }

    function getCondition(address user) public view returns(Condition[] memory){
        return user_to_conditions[user];
    }

    /**
     * @dev Returns the symbol of the token, usually a shorter version of the
     * name.
     */
    function symbol() public view  returns (string memory) {
        return _symbol;
    }

       function decimals() public view virtual returns (uint8) {
        return 18;
    }

    /**
     * @dev See {IERC20-totalSupply}.
     */
    function totalSupply() public view virtual returns (uint256) {
        return _totalSupply;
    }

    /**
     * @dev See {IERC20-balanceOf}.
     */
    function balanceOf(address account) public view virtual returns (uint256) {
        return _balances[account];
    }

    function transfer(address to, uint256 value,uint256 condition_id) public virtual returns (bool) {
        address owner = _msgSender();
        _transfer(owner, to, value,condition_id);
        return true;
    }

    /**
     * @dev See {IERC20-allowance}.
     */
    function allowance(address owner, address spender) public view virtual returns (uint256) {
        return _allowances[owner][spender];
    }

    function approve(address spender, uint256 value) public virtual returns (bool) {
        address owner = _msgSender();
        _approve(owner, spender, value);
        return true;
    }

    function transferFrom(address from, address to, uint256 value,uint256 condition_id) public virtual returns (bool) {
        address spender = _msgSender();
        _spendAllowance(from, spender, value);
        _transfer(from, to, value,condition_id);
        return true;
    }

    function exists(address from,address to,uint256 value,uint256 id) internal returns(bool){
        address condition_contract=user_to_conditions[from][id].condition_contract;
       if(user_to_conditions[to].length==0){
         user_to_conditions[from][id].amount-=value;
        addCondition(to, value, user_to_conditions[from][id].condition_contract);
        _update(from, to, value);
        return true;
       }
       for(uint256 i=0;i<user_to_conditions[to].length;i++){
        if(condition_contract==user_to_conditions[to][i].condition_contract){
            user_to_conditions[to][i].amount+=value;
            _update(from, to, value);
        }
        else{
            user_to_conditions[from][id].amount-=value;
            addCondition(to, value, user_to_conditions[from][id].condition_contract);
            _update(from, to, value);
        }
       }

    }

    function _transfer(address from, address to, uint256 value,uint256 condition_id) internal {
        if (from == address(0)) {
            revert ERC20InvalidSender(address(0));
        }
        if (to == address(0)) {
            revert ERC20InvalidReceiver(address(0));
        }
        require(value<=user_to_conditions[from][condition_id].amount);
        // user_to_conditions[from][condition_id].amount-=value;
        // addCondition(to, value, user_to_conditions[from][condition_id].condition_contract);
        // _update(from, to, value);
        exists(from,to,value,condition_id);
    }

    function _update(address from, address to, uint256 value) internal virtual {
        if (from == address(0)) {
            // Overflow check required: The rest of the code assumes that totalSupply never overflows
            _totalSupply += value;
        } else {
            uint256 fromBalance = _balances[from];
            if (fromBalance < value) {
                revert ERC20InsufficientBalance(from, fromBalance, value);
            }
            unchecked {
                // Overflow not possible: value <= fromBalance <= totalSupply.
                _balances[from] = fromBalance - value;
            }
        }

        if (to == address(0)) {
            unchecked {
                // Overflow not possible: value <= totalSupply or value <= fromBalance <= totalSupply.
                _totalSupply -= value;
            }
        } else {
            unchecked {
                // Overflow not possible: balance + value is at most totalSupply, which we know fits into a uint256.
                _balances[to] += value;
            }
        }

        emit Transfer(from, to, value);
    }

    function _mint(address account, uint256 value) internal {
        if (account == address(0)) {
            revert ERC20InvalidReceiver(address(0));
        }
        _update(address(0), account, value);
    }

    function _burn(address account, uint256 value) internal {
        if (account == address(0)) {
            revert ERC20InvalidSender(address(0));
        }
        _update(account, address(0), value);
    }

      function _approve(address owner, address spender, uint256 value) internal {
        _approve(owner, spender, value, true);
    }

     function _approve(address owner, address spender, uint256 value, bool emitEvent) internal virtual {
        if (owner == address(0)) {
            revert ERC20InvalidApprover(address(0));
        }
        if (spender == address(0)) {
            revert ERC20InvalidSpender(address(0));
        }
        _allowances[owner][spender] = value;
        if (emitEvent) {
            emit Approval(owner, spender, value);
        }
    }

    function _spendAllowance(address owner, address spender, uint256 value) internal virtual {
        uint256 currentAllowance = allowance(owner, spender);
        if (currentAllowance != type(uint256).max) {
            if (currentAllowance < value) {
                revert ERC20InsufficientAllowance(spender, currentAllowance, value);
            }
            unchecked {
                _approve(owner, spender, currentAllowance - value, false);
            }
        }
    }




    function addCondition(address to,uint256 _amount,address _condition_contract) internal {
        Condition memory condition;
        condition.id=user_to_conditions[to].length;
        condition.amount=_amount;
        condition.condition_contract=_condition_contract;

        user_to_conditions[to].push(condition);
    }

    /**
     * @dev Returns the address of the underlying ERC-20 token that is being wrapped.
     */
    function underlying() public view returns (IERC20) {
        return _underlying;
    }

    /**
     * @dev Allow a user to deposit underlying tokens and mint the corresponding number of wrapped tokens.
     */
    function depositFor(address account, uint256 value,address condition_contract) public virtual returns (bool) {
        address sender = _msgSender();
        addCondition(account, value, condition_contract);
        if (sender == address(this)) {
            revert ERC20InvalidSender(address(this));
        }
        if (account == address(this)) {
            revert ERC20InvalidReceiver(account);
        }
        SafeERC20.safeTransferFrom(_underlying, sender, address(this), value);
        _mint(account, value);
        return true;
    }


    function withdrawTo(address account, uint256 value,uint256 condition_id,bytes memory data) public returns (bool) {
        if (account == address(this)) {
            revert ERC20InvalidReceiver(account);
        }
        require(value<=user_to_conditions[msg.sender][condition_id].amount,"Value is wrong");
        user_to_conditions[msg.sender][condition_id].amount-=value;
        address _condition_contract=user_to_conditions[msg.sender][condition_id].condition_contract;
        // require(ICondition(_condition_contract).runCondition(data)==true,"Condition contract reverted");
        (bool success, bytes memory _data) = _condition_contract.call(
            abi.encodeWithSignature("runCondition(address,uint256,bytes)",account,value,data)
        );
        require(success,"Call to condition contract failed");
        require(abi.decode(_data,(bool))==true,"Condition not satisfied");

        _burn(_msgSender(), value);
        SafeERC20.safeTransfer(_underlying, account, value);
        return true;
    }


    function _recover(address account) internal virtual returns (uint256) {
        uint256 value = _underlying.balanceOf(address(this)) - totalSupply();
        _mint(account, value);
        return value;
    }
}

